using System.Net;
using Azure.Communication.Email;
using Microsoft.ApplicationInsights;
using Microsoft.AspNetCore.Authorization;
using Microsoft.AspNetCore.Mvc;
using woodgroveapi.Helpers;
using woodgroveapi.Models;

namespace woodgroveapi.Controllers;


//[Authorize]
[ApiController]
[Route("[controller]")]
public class OnOtpSendController : ControllerBase
{
    private readonly ILogger<OnOtpSendController> _logger;
    private readonly IConfiguration _configuration;
    private TelemetryClient _telemetry;

    public OnOtpSendController(ILogger<OnOtpSendController> logger, TelemetryClient telemetry, IConfiguration configuration)
    {
        _logger = logger;
        _configuration = configuration;
        _telemetry = telemetry;
    }

    [HttpPost(Name = "OnOtpSend")]
    public async Task<OnOtpSendResponse> PostAsync([FromBody] OnOtpSendRequest requestPayload)
    {
        // Track the page view 
        IDictionary<string, string> moreProperties = new Dictionary<string, string>();
        if (requestPayload.data.otpContext.identifier.IndexOf("@") > 0)
            moreProperties.Add("Identifier", requestPayload.data.otpContext.identifier.Substring(0, 1) + "_" + requestPayload.data.otpContext.identifier.Split("@")[1]);
        AppInsightsHelper.TrackApi("OnOtpSend", this._telemetry, requestPayload.data, moreProperties);

        //For Azure App Service with Easy Auth, validate the azp claim value
        if (!AzureAppServiceClaimsHeader.Authorize(this.Request))
        {
            AppInsightsHelper.TrackError(new Exception("Unauthorized"), this._telemetry, requestPayload.data);
            Response.StatusCode = (int)HttpStatusCode.Unauthorized;
            return null;
        }

        try
        {


            var emailClient = new EmailClient(_configuration.GetSection("AppSettings:EmailConnectionString").Value);

            var subject = "Your Woodgrove account verification code";
            var htmlContent = @$"<html><body>
            <div class='inbox-data-content-intro'> 
 
                <table dir='ltr'>
                    <tbody>
                    <tr><td style='padding: 0px; font-family: &quot;Segoe UI Semibold&quot;, &quot;Segoe UI Bold&quot;, &quot;Segoe UI&quot;, &quot;Helvetica Neue Medium&quot;, Arial, sans-serif; font-size: 17px; color: rgb(112, 112, 112);'><img style='width: 55px;' src='data:image/png;base64, ' alt='Red dot' /> Woodgrove Groceries</td></tr>
                    <tr><td style='padding: 0px; font-family: &quot;Segoe UI Light&quot;, &quot;Segoe UI&quot;, &quot;Helvetica Neue Medium&quot;, Arial, sans-serif; font-size: 41px; color: rgb(38, 114, 236);'>Account verification code</td></tr>
                    <tr><td style='padding: 25px 0px 0px; font-family: &quot;Segoe UI&quot;, Tahoma, Verdana, Arial, sans-serif; font-size: 14px; color: rgb(42, 42, 42);'>
                                
                                To access <span style='font-family: &quot;Segoe UI Bold&quot;, &quot;Segoe UI Semibold&quot;, &quot;Segoe UI&quot;, &quot;Helvetica Neue Medium&quot;, Arial, sans-serif; font-size: 14px; font-weight: bold; color: rgb(42, 42, 42);'>Woodgrove Groceries</span>'s apps and resources, please use the code below for account verification. The code will only work for 30 minutes.
                            </td></tr>
                    <tr><td style='padding: 25px 0px 0px; font-family: &quot;Segoe UI&quot;, Tahoma, Verdana, Arial, sans-serif; font-size: 14px; color: rgb(42, 42, 42);'>Account verification code:</td></tr>
                    <tr><td style='padding: 0px; font-family: &quot;Segoe UI Bold&quot;, &quot;Segoe UI Semibold&quot;, &quot;Segoe UI&quot;, &quot;Helvetica Neue Medium&quot;, Arial, sans-serif; font-size: 20px; font-weight: bold; color: rgb(42, 42, 42);'>
                    {requestPayload.data.otpContext.onetimecode}</td></tr>
                    <tr><td style='padding: 25px 0px 0px; font-family: &quot;Segoe UI&quot;, Tahoma, Verdana, Arial, sans-serif; font-size: 14px; color: rgb(42, 42, 42);'>
                                
                                If you didn't request a code, you can ignore this email.
                            </td></tr>
                </tbody></table>
                
                <div style='margin-top: 10px;'>
                
                Sincerely, <a href='https://woodgrovedemo.com'>Woodgove Corporation</a></div></div>

                <div lang='en' style='margin-top: 20px; margin-bottom: 10px;'><a href='https://woodgrovedemo.com/Privacy' style='color: rgb(38, 114, 236); text-decoration: none;'>Privacy Statement</a>
            </div></body></html>";

            var sender = "donotreply@woodgrovedemo.com";

            EmailSendOperation emailSendOperation = await emailClient.SendAsync(
                Azure.WaitUntil.Started,
                sender,
                requestPayload.data.otpContext.identifier,
                subject,
                htmlContent);

        }
        catch (System.Exception ex)
        {
            AppInsightsHelper.TrackError(ex, this._telemetry, requestPayload.data);
        }
        
        return new OnOtpSendResponse();
    }


}